package mypackage;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
        System.out.println("Original x = " + x);
        System.out.println("x++ = " + (x++)); 
        System.out.println("After x++ x = " + x);
        System.out.println("++x = " + (++x)); 
        System.out.println("x-- = " + (x--)); 
        System.out.println("After x-- x = " + x);
        System.out.println("--x = " + (--x)); 
}


	}

